import './App.css';

import React, { Component } from 'react'

export default class App extends Component {

  constructor(props){
    super(props);

      this.state={
        todoList:[],
        activateItem:{
          id:null,
          title:'',
          completed:false
        },
        editing:false,
      }

      // this.fetchTask = this.fetchTask.bind(this)
  };

  componentDidMount(){
    this.fetchTask()
  }

  fetchTask(){
    console.log("fetching.")
    fetch("http://127.0.0.1:8000/")
    .then(Response => Response.json())
    .then(data => 
      this.setState({
        todoList:data
      })
      )
  }

  render() {
    var tasks = this.state.todoList

    return (
      <div>
        <>
          <div className="container mx-auto shadow-lg mt-5">
            <form id='form' className='p-2'>
              <div className="form">
                <input type="text" className="form-control p-2 mt-5" id="title" placeholder="Enter text"/>
              </div>
              <button type="submit" className="btn btn-danger mt-5 mb-5  ">SUBMIT</button>
            </form>
          </div>
          <div className="container mx-auto shadow-lg mt-5">
            <div id="list-wrapper">
              {
                tasks.map(function(task, index){
                  return(
                    <div key={index} className='task-wrapper flex-wrapper'>
                      <span>{task.title}</span>
                    </div>
                  )
                })
              }
            </div>
          </div>
        </>
      </div>
    )
  }
}
